<template>    <div class="time">        <el-date-picker                v-model="time"                type="daterange"                align="right"                unlink-panels                range-separator="至"                start-placeholder="开始日期"                end-placeholder="结束日期"                value-format="yyyy-MM-dd"                @change="change">        </el-date-picker>    </div></template><script>    import {bus} from "../../util/tools/eventBus";    export default {        data(){            return{                time:[this.getToday(),this.getToday()],            }        },        mounted(){            this.$emit('selectTime',this.time)        },        methods:{            getToday(){                var nowDate = new Date();                var year = nowDate.getFullYear();                var month = nowDate.getMonth() + 1 < 10 ? "0" + (nowDate.getMonth() + 1)                    : nowDate.getMonth() + 1;                var day = nowDate.getDate() < 10 ? "0" + nowDate.getDate() : nowDate                    .getDate();                var dateStr = year + "-" + month + "-" + day;                return dateStr            },            emit(name,data){                bus.$emit(name,data)            },            change(){                this.$emit('selectTime',this.time)            },        },    }</script>